import React from 'react';

function ImageComponent({ productImage }) {
  const base64Image = btoa(String.fromCharCode(...new Uint8Array(productImage)));

  return (
    <div>
      <img src={`data:image/png;base64,${base64Image}`} alt="Product" />
    </div>
  );
}

export default ImageComponent;
